library(TrajConserve); if(file.exists("data/small_example.Rds")) { small_epi <- readRDS("data/small_example.Rds"); trajectory_data <- seurat_to_trajectory_array(seurat_obj = small_epi, assay = "originalexp", pseudo_col = "pseudo", project_col = "Project"); print("Data loaded successfully"); print("Using the rstan backend"); gene_idx <- 10; system.time({ model_test <- try(run_trajectory_model(trajectory_data$reshaped_data, gene_idx, backend = "rstan")) }); if(!inherits(model_test, "try-error")) print("Model ran successfully!") }
